IMPORT
}
enum ItemRestrictionType {
OBSERVATION
PROHIBITION
RESTRICTION
}
enum Mode {
LIVE
TEST
}
input ItemRestrictionApplyInput {
items: [ItemRestrictionInput!]!
restrictionTypeThreshold: ItemRestrictionType = PROHIBITION
